import React from 'react';
import { TextInput } from 'react-native';
import { colors } from '../../styles/colors';

class TextArea extends React.Component {
  render() {
    const { value, onChangeText, placeholder, style, ...rest } = this.props;

    return (
      <TextInput
        {...rest}
        style={[styles.textarea, style]}  // Aplica os estilos e qualquer outro estilo extra
        value={value}
        onChangeText={onChangeText}
        placeholder={placeholder}
        multiline={true}  // Permite múltiplas linhas
        numberOfLines={4}  // Define o número de linhas visíveis inicialmente
      />
    );
  }
}

const styles = {
  textarea: {
    marginTop: 24,
    padding: 16,
    borderRadius: 8,
    backgroundColor: colors.gelo,  // Cor de fundo do textarea
    width: '100%',  // Ocupa a largura total disponível
    height: 100,    // Define uma altura mínima
    textAlignVertical: 'top',  // Alinha o texto no topo
  },
};

export default TextArea;

