import React from "react";
import { TextInput, View, Text, TouchableOpacity } from "react-native";
import { colors } from "../../styles/colors";
import { fonts } from "../../styles/fontsfamily";
import Icon from "react-native-vector-icons/FontAwesome"; 

class Input extends React.Component {
  state = {
    isPasswordVisible: false,
  };

  togglePasswordVisibility = () => {
    this.setState((prevState) => ({
      isPasswordVisible: !prevState.isPasswordVisible,
    }));
  };

  render() {
    const { value, onChangeText, inputStyle, labelStyle, placeholder, secureTextEntry } = this.props;
    const { isPasswordVisible } = this.state;

    return (
      <View style={styles.container}>
        <View style={styles.inputContainer}>
          <TextInput
            style={[styles.input, inputStyle]}
            value={value}
            onChangeText={onChangeText}
            placeholder={placeholder}
            secureTextEntry={!isPasswordVisible}
          />
          {secureTextEntry && (
            <TouchableOpacity
              onPress={this.togglePasswordVisibility}
              style={styles.icon}
            >
              <Icon name={isPasswordVisible ? "eye-slash" : "eye"} size={20} color={colors.azulcinzento} />
            </TouchableOpacity>
          )}
        </View>
      </View>
    );
  }
}

const styles = {
  container: {
    marginBottom: 16,
  },
  inputContainer: {
    flexDirection: "row", 
    alignItems: "center",
    position: "relative", 
  },
  input: {
    height: 50,
    backgroundColor: colors.gelo,
    borderColor: colors.gelo,
    borderWidth: 1,
    borderRadius: 30,
    paddingLeft: 20, 
    paddingRight: 40,
    fontSize: 18,
    fontFamily: fonts.montserrat,
    outline: "none",
    color: colors.azulcinzento,
    flex: 1,
    margin: 5,
    MarginLeft: 15,
    MarginRight: 15,
  },
  icon: {
    position: "absolute",
    right: 18,
    top: "50%", 
    transform: [{ translateY: -12 }], 
  },
};

export default Input;

import React from "react";
import { Text, View, Image, TouchableOpacity } from "react-native";
import Input from "../../components/input/input";
import Botao from "../../components/button/button";
import { colors } from "../../styles/colors";

export default function Login() {
  const handleForgotPassword = () => {
    console.log("Recuperar senha clicado");
  };

  return (
    <View className="flex-1">
      <View className="pt-6 justify-center items-center">
        <Text className="text-8xl font-thin">Vincula</Text>
        <View className="flex-row items-center mt-4">
          <Image
            source={require("../../assets/Logo.svg")}
            style={{ width: 100, height: 100, marginRight: 20 }}
          />
          <Text className="text-8xl font-thin">Ação</Text>
        </View>
      </View>

      <View className="flex-1 justify-center items-center">
        <Text className="text-4xl font-montserrat font-bold mb-6">Bem-vindo de volta!</Text>
        <Text className="text-xl text-center px-4">
          Vamos juntos transformar vidas. Entre para continuar conectando
          solidariedade.
        </Text>
      </View>

      <View className="justify-center items-center">
        <View className="justify-center items-end">
          <Input
            label="E-mail"
            placeholder="E-mail"
            keyboardType="email-address"
          />
          <Input label="Senha" placeholder="Senha" secureTextEntry={true} />

          <TouchableOpacity onPress={handleForgotPassword}>
